@MEMLIB;************************;MEMORY LIBRARY;PARAMS ARE:;E - PAGE NEEDED;A - MEMORY WINDOW (EG #4000,#8000,#C000);; !!!!attention!!!;DON'T FORGET ABOUT BIT 7 IN dos7FFD (#FF7 PORTS);;SIMPLY PAGING UNIT;USES: A,E,BC;RETURNS: NONELOCAL         OR      A        JP      Z,PGCH0        DEC     A        JP      Z,PGCH1                                ;PAGE ADDRESSES ARE #C000 - FFFF        LD      A,E        LD      BC,MEM3        OUT     (C),A        RET PGCH0   LD      A,E        LD      BC,MEM1        OUT     (C),A        RET PGCH1   LD      A,E        LD      BC,MEM2        OUT     (C),A        RET         ENDL 