SHAD    EQU     #BFMEM0    EQU     #37F7MEM1    EQU     #77F7MEM2    EQU     #B7F7MEM3    EQU     #F7F7MMU3    EQU     #FFF7MMU2    EQU     #BFF7MMU1    EQU     #7FF7MMU0    EQU     #3FF7VIDEO   EQU     #EFF7VIDSHAD EQU     #FF77TRUE    EQU     1FALSE   EQU     0        ORG     #8000        DI         LD      SP,#C000        LD      A,TRUE        XOR     A               ;SHADOW PORTS _FALSE_        CALL    SHADOW        LD      BC,VIDEO        OUT     (C),A        LD      A,TRUE        CALL    SHADOW        LD      A,#8            ;TURBO + 320*200*16 SCREEN        LD      BC,VIDSHAD        OUT     (C),A        LD      A,#7F        LD      BC,#FFF7        OUT     (C),A        LD      BC,40*200       ;CLEARS SCREEN        LD      HL,#C000        LD      DE,#2000LP1        LD      A,1~        CALL    PAGECH3        XOR     A               ; LET`S CLEAN THE SCREEN        LD      (HL),A          ;CLEAN BYTE0        SET     5,H        LD      (HL),A          ;CLEAN IT TOO        RES     5,H        LD      A,5~        CALL    PAGECH3        XOR     A        LD      (HL),A        SET     5,H        LD      (HL),A        RES     5,H        INC     HL        DEC     BC        LD      A,B        OR      C        JR      NZ,LP1        LD      HL,#C000        LD      A,222        LD      B,ALP2     EX      AF,AF'        LD      A,5~        CALL    PAGECH3        EX      AF,AF'        LD      (HL),A        SET     5,H        LD      (HL),A        RES     5,H        EX      AF,AF'        LD      A,1~        CALL    PAGECH3        EX      AF,AF'        LD      (HL),A        SET     5,H        LD      (HL),A        RES     5,H        INC     HL        DJNZ    LP2        DI         HALT PAGECH0 PUSH    BC        LD      BC,MEM0        OUT     (C),A        POP     BC        RET PAGECH3 PUSH    BC        LD      BC,MEM3        OUT     (C),A        POP     BC        RET SHADOW        OUT     (SHAD),A        RET 